def clean_baseball_list(baseball_list):
    # Remove any groups of two teams that do not have two players in between them
    new_list = []
    i = 0
    while i < len(baseball_list) - 3:
        team1 = baseball_list[i]
        team2 = baseball_list[i+1]
        player1 = baseball_list[i+2]
        player2 = baseball_list[i+3]
        if player1 != '' and player2 != '':
            new_list += [team1, team2, player1, player2]
            i += 4
        else:
            i += 2

    # If there are any leftover items, add them to the new list
    if i < len(baseball_list):
        new_list += baseball_list[i:]

    return new_list


